import React, { Component } from 'react';
import Web3 from 'web3';
import './App.css';
import Meme from '../abis/Meme.json';
import Authenticity from '../abis/Authenticity.json';
import CryptoJS from 'crypto-js'



const ipfsClient = require('ipfs-http-client')
const ipfs = ipfsClient({ host: 'ipfs.infura.io', port: 5001, protocol: 'https' }) // leaving out the arguments will default to these values

class App extends Component {

  async componentWillMount() {
    await this.loadWeb3()
    await this.loadBlockchainData()
  }

  //Get the account
  //Get the network
  //Get Smart Contract
  //---> ABI:Meme.abi( in Meme.json)
  //---> Address:networkData.address
  // Get Meme Hash

  async loadWeb3() {
    if (window.ethereum) {
      window.web3 = new Web3(window.ethereum)
      await window.ethereum.request({method: 'eth_requestAccounts'});
    }
    else if (window.web3) {
      window.web3 = new Web3(window.web3.currentProvider)
    }
    else {
      window.alert('Non-Ethereum browser detected. You should consider trying MetaMask!')
    }
  }

  async loadBlockchainData() {
    const web3 = window.web3
    // Load account
    const accounts = await web3.eth.getAccounts()
    console.log(accounts)
    this.setState({ account: accounts[0] })
    const networkId = await web3.eth.net.getId()
    console.log("networkId is: "+ networkId)
    const networkData = Meme.networks[networkId]
    if(networkData) {
      // Fetch the contract
      const abi= Meme.abi
      const address = networkData.address
      const contract = web3.eth.Contract(abi,address)
      this.setState({ contract })
      console.log(contract)
      const memeHash = await contract.methods.get().call()
      console.log(memeHash)
      this.setState({memeHash }, () => {
            console.log(this.state.memeHash, 'Hash after');
      }); 
    } else {
     window.alert('Smart contract Meme not deployed to detected network.')
    }

    const networkData2=Authenticity.networks[networkId];
    if(networkData2) {
      // Fetch the contract
      const Authenticity_abi= Authenticity.abi
      this.state2.Authenticity_address = networkData2.address
      this.state2.Authenticity_contract = web3.eth.Contract(Authenticity_abi,this.state2.Authenticity_address);
      //this.setState({ Authenticity_contract })
      console.log(this.state2.Authenticity_contract)
      //const memeHash = await contract.methods.get().call()
      //console.log(memeHash)
      //this.setState({memeHash }, () => {
            //console.log(this.state.memeHash, 'Hash after');
      //}); 
    } else {
     window.alert('Smart contract Authenticity not deployed to detected network.')
    }

  }

  constructor(props) {
    super(props);
    this.state = {
      buffer: null,
      memeHash:'QmeesNVWTooMQgqmszfcWhjdd7fvVmGbvVKVGLsThqTqk1',
      contract: null,
      web3: null,
      account: null,
      ethAddress:'0x0BFe599eaE812F3900F2eB77Fb3608fe00E48a25',
      blockNumber:'',
      transactionHash:'',
      gasUsed:'',
      txReceipt: null   
    };

    this.state2={
      Authenticity_contract:"",
      Authenticity_address:"",

      fileHash: "",
      fileSize: "",
      fileExtension:"",
      fileDetails:null,

      hash:""

    }
  }

  captureFile = (event) => {
    event.preventDefault()
    //console.log('file captured...')
    //Process file for IPFS
    //console.log(event.target.files)
    const file = event.target.files[0]
    const reader = new window.FileReader()
    reader.readAsArrayBuffer(file)
    reader.onloadend = () => {
      this.setState({ buffer: Buffer(reader.result) })
      console.log('buffer', this.state.buffer)
      console.log('Ipfs Hash', this.state.memeHash)
      console.log('buffer', Buffer(reader.result) )
    }
  }

  getFileExtension(fileName) {
    /*TODO*/
    return fileName.split('.').pop();
  }

  async getFileHash(file){
    // $("#myform").ajaxForm(function (data) {
    //   window.alert("ajax success")
    //   //window.alert(JSON.stringify(data));

    //   var hash=JSON.stringify(data);

    //   var hashElement = document.getElementById('hash');
    //   hash=hash.substr(1,64);
    // });
    // return hash;
    //var fileInput = document.getElementById('inputFile');
    //var file = fileInput.files[0];

    //let reader = new FileReader();
    // //var fileBS=reader.readAsBinaryString(file);  
    // //var hash = CryptoJS.SHA256(fileBS).toString();
    // //hash=hash.toString(CryptoJS.enc.Hex);

    // var sha256;

    // reader.readAsBinaryString(file);


    // reader.onload = function(event) {
    // var binary = event.target.result;
    // sha256 = CryptoJS.SHA256(binary).toString();
    // console.log("SHA256 hash: ", sha256);
    // };

    //let filepath = file.filepath;

    var sha256 = CryptoJS.algo.SHA256.create();

    sha256.update(file);

    //var fileBuffer = reader.readAsText(file);

    var hash = sha256.finalize().toString();

    console.log("hash is: ", hash);

    this.state2.hash=hash;

    return hash;
  }



  upload=(event)=>{
      var fileInput = document.getElementById('inputFile');
      var file = fileInput.files[0];

      var fileHash=this.getFileHash(file);
      var fileExtension=this.getFileExtension(file.name);
      var fileSize=file.size;

      this.state2.fileHash=fileHash;
      this.state2.fileExtension=fileExtension;
      this.state2.fileSize=fileSize;

      var metadataElement=document.getElementById("metadata");
      metadataElement.innerHTML="file hash: " + fileHash+ "<br> file extension: " + fileExtension + "<br> file size: "+ fileSize+"<br>" ;
  }




  //Example hash:QmeesNVWTooMQgqmszfcWhjdd7fvVmGbvVKVGLsThqTqk1
  //Example url:https://ipfs.infura.io/ipfs/QmeesNVWTooMQgqmszfcWhjdd7fvVmGbvVKVGLsThqTqk1
  //QmehCAvXyj8RFp5pEow6wpba5g9g4oHeTyutEzLP9yDido
  onSubmit =  (event) => {
    //const web3 = window.web3
    event.preventDefault()
    console.log("Submitting file to ipfs...")

    //obtain contract address from storehash.js
    const ethAddress= this.state.contract.options.address;
    console.log("ethAddress before",ethAddress)
    this.setState({ ethAddress }, () => {
            console.log(this.state.ethAddress, 'ethAddress after');
    }); 

    ipfs.add(this.state.buffer, (error, result) => {
        console.log('Ipfs result', result)
        const memeHash = result[0].hash
        console.log('Ipfs Hash (in ipfs add function)', this.state.memeHash)
        this.setState({blockNumber:"waiting.."}, () => {
            console.log(this.state.blockNumber, 'blockNumber 1');
        }); 
        this.setState({gasUsed: "waiting..."}, () => {
            console.log(this.state.gasUsed, 'gasUsed 1');
        }); 
        this.setState({ memeHash }, () => {
            console.log(this.state.memeHash, 'Hash after');
        }); 
       if(error) {
        console.error(error)
        return
        }
        // this.state.contract.methods.set(result[0].hash).send({
        //   from: this.state.account}, (error, transactionHash) => {
        //    console.log('transactionHash of Meme: ',transactionHash)
        //    this.setState({transactionHash});
        //    console.log('transactionHash of Meme: ', this.state.transactionHash)
        // });

        // const txReceipt = web3.eth.getTransaction(this.state.transactionHash)
        // txReceipt.then((txReceipt) => this.setState({ txReceipt }))
        // console.log('txReceipt1', this.state.txReceipt)
        // this.setState({gasUsed: txReceipt.gasUsed}, () => {
        //   console.log(this.state.gasUsed, 'gas is Used'); 
        // }); 
        console.log("fileSize is: ", this.state2.fileSize);
        console.log("fileHash is: ", this.state2.fileHash);
        console.log("fileExtension is: ", this.state2.fileExtension);

        console.log("Authenticity function is responding: ");
        this.state2.Authenticity_contract.methods.certifyFile(this.state2.fileSize, this.state2.fileHash , this.state2.fileExtension)
        .send({ from: this.state.account })
        .on("receipt", function(receipt) { 
            console.log("receipt contractAddress: ", receipt.contractAddress);
            //$("#txStatus").text("file size: " + fileSize + "\n"+ "file hash: " + fileHash + "\n" + "file extension: "+ fileExtension+"\n" );
            // 事务被区块链接受了，重新渲染界面
            // getZombiesByOwner(userAccount).then(displayFiles);
        })
        .on('transactionHash', function(transactionHash){
            console.log("transactionHash for authenticity:  ", transactionHash);
        })
        .on("error", function(error) {
        // 告诉用户合约失败了
            console.log("error: "+error);

       });



    })

     // Step 2: store file on blockchain




      //this.setState({blockNumber:"waiting.."});
      //this.setState({gasUsed:"waiting..."});

      // get Transaction Receipt in console on click
      // See: https://web3js.readthedocs.io/en/1.0/web3-eth.html#gettransactionreceipt
      //web3.eth.getTransaction(this.state.transactionHash)
      //.then(console.log);
      //web3.eth.getTransaction(this.state.transactionHash, (err, txReceipt)=>{
        //this.setState({ txReceipt }, () => {
           // console.log(this.state.txReceipt, 'txReceipt');
        //}); 
     // }); //await for getTransactionReceipt
     //0x0BFe599eaE812F3900F2eB77Fb3608fe00E48a25

  }

  onSubmit2 =  (event) => {

    //var fileInput = document.getElementById('inputFile2');
    //var file = fileInput.files[0];

    //var fileHash=this.getFileHash(file);


    console.log("this is verify file");

    this.state2.Authenticity_contract.methods.verifyFile("4ea5c508a6566e76240543f8feb06fd457777be39549c4016436afda65d2330e").call().then(function(result) {
      console.log("file details are: " + JSON.stringify(result));
      var fileDetails=JSON.stringify(result);
      this.state2.fileDetails=fileDetails;

      // var author=document.getElementById("author");
      // var fileHash=document.getElementById("fileHash");
      // var timestamp=document.getElementById("timestamp");
      // var fileSize=document.getElementById("fileSize");
      // var fileExtension=document.getElementById("fileExtension");
      // //var txHash=document.getElementById("transactionHash");

      // author.innerHTML=fileDetails.author;
      // fileHash.innerHTML=fileDetails.fileHash;
      // timestamp.innerHTML=fileDetails.timestamp;
      // fileSize.innerHTML=fileDetails.fileSize;
      // fileExtension.innerHTML=fileDetails.fileExtension;




    });







  }










  render() {
    return (
      <div>
        <nav className="navbar navbar-dark fixed-top bg-dark flex-md-nowrap p-0 shadow">
          <a
            className="navbar-brand col-sm-3 col-md-2 mr-0"
            href="http://www.dappuniversity.com/bootcamp"
            target="_blank"
            rel="noopener noreferrer"
          >
            Ethereum Dapp With IPFS 
          </a>
          <ul className="navbar-nav px-3">
            <li className="nav-item text-nowrap d-none d-sm-none d-sm-block">
              <small className="text-white">{this.state.account}</small>
            </li>
          </ul>
        </nav>
        <div className="container-fluid mt-5">
          <div className="row">
            <main role="main" className="col-lg-12 d-flex text-center">
              <div className="content mr-auto ml-auto">
                <a
                  href="http://www.dappuniversity.com/bootcamp"
                  target="_blank"
                  rel="noopener noreferrer"
                >
                  <img src={`https://ipfs.infura.io/ipfs/${this.state.memeHash}`} alt="logo" />
                </a>
                <h4> IPFS Hash: {this.state.memeHash} </h4>
                <h4> Ethereum contract address: {this.state.ethAddress} </h4>
                <p>&nbsp;</p>



                <h2>Step1: Upload File</h2>
                <form onSubmit={this.onSubmit} id="myform">
                  <input id='inputFile' type='file' onChange={this.captureFile} />
                  <button onClick={this.upload} type="button">Upload</button>

                  <h2>Step 2：Review the metadata</h2>
                  <div id="metadata"></div>


                  <h2>Step3: Timestamp the metadata into the blockchain</h2>
                  <input type='submit' value="submit"/>


                </form>

                <hr/>

                <div>
                <form onSubmit={this.onSubmit2} id="myform2" target='hidden' method='GET'>
                  <input id='inputFile2' type='file' />              
                  <input type='submit' value="submit"/>
                </form>

